import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import PropTypes from 'prop-types';
import "../styles/Login.css";
import axios from "axios";
import * as constants from './Constants';
import { Modal } from "react-bootstrap"
import "bootstrap/dist/css/bootstrap.min.css"
import { useNavigate } from 'react-router-dom';

export default function Login({ setToken }) {
  const [username, setUsername] = useState("");
  const [show, setShow] = useState(false);
  const [message, setMessage] = useState("")
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const handleMessage= (text) => setMessage(text)
  const navigate = useNavigate();

  function validateForm() {
    return username.length >= 8 && !username.includes("#");
  }

  async function handleSubmit(event) {
    event.preventDefault();
    try {
      const response = await axios.post(constants.HOST + "/login", {
        username: username
      });
      let token = response.data.token;
      setToken(token)
      window.location.reload(false);
    } catch (error) {
      handleMessage("Login Failed")
      handleShow();
      console.log('hi', error);
    }
  }

  return (
    <div className="Login">
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Message</Modal.Title>
        </Modal.Header>
        <Modal.Body>{message}</Modal.Body>
        <Modal.Footer>
          <Button variant="Primary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
      <Form onSubmit={handleSubmit}>
        <Form.Group size="lg" controlId="username">
          <Form.Label>Username</Form.Label>
          <p>(Must be 8 characters long and not have "#"</p>
          <Form.Control
            autoFocus
            type="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </Form.Group>
        <Button size="lg" type="submit" disabled={!validateForm()}>
          Go!
        </Button>
      </Form>
    </div>
  );
}

Login.propTypes = {
  setToken: PropTypes.func.isRequired
}
