  //       return false;
  //     }
  //     return true;
  //   } catch (err) {
  //     console.log(err)
  //   }
  // }

  const tokenExists = (token) => {
      axios.get(CONSTANTS.HOST + "/user", {
        headers: {
          "Authorization": "Bearer " + token.token
        }
      }).then(response => {
        console.log(response)
        return true;
      }).catch(error => {
        console.log(error)
        return false;
      })
  }

  return {
    token,
    setToken: saveToken,
    checkVerifiedToken: verifyToken,
    tokenExists
  }
}
