import React, { useState, useEffect } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import "../styles/Game.css";
import axios from "axios";
import { useLocation } from "react-router";
import 'react-chatbox-component/dist/style.css';
import { ChatBox } from 'react-chatbox-component';
import io from "socket.io-client";
import $ from "jquery"


export default function Game(roomID) {
  const [room, setRoom] = useState("");
  const { state } = useLocation();
  let socket;
  const handleChat = (message) => {
    if (message) {
      socket.emit("message", {
        text: message
      })
    }
  }

  const messages = [
    {
      "text": "Hello there",
      id: "",
      "sender": {
        "name": "Ironman",
        "uid": "user1",
        "avatar": "https://singlecolorimage.com/get/000000/400x100",
      },
    },
  ]
  const cardArray = []
  useEffect(() => {
    socket = io("http://localhost:3003/chat");
    for (let i = 0; i < 10; i++) {
      cardArray.push(
        <rect width="100" height="200" />
      )
    }
  }, [])

  return (
    <div>
      <div className="Game">
        {/* <img src="/img/abstract_scene.svg" className="Card" height="200" width="150"/> */}
        <svg id="svg" xmlns="http://www.w3.org/2000/svg">
          {cardArray}
        </svg>
      </div>
      <div className="Chat">
        <ChatBox messages={messages} onSubmit={handleChat} />
      </div>
    </div>
  );
}
