import React, { useState, useEffect, useRef } from "react";
//import Form from "react-bootstrap/Form";
//import Button from "react-bootstrap/Button";
import "../styles/Game.css";
//import axios from "axios";
//import { useLocation } from "react-router";
import 'react-chatbox-component/dist/style.css';
import { ChatBox } from 'react-chatbox-component';
import io from "socket.io-client";
//import $ from "jquery";
import useToken from "../components/useToken";



export default function Game(roomID) {
  //const [room, setRoom] = useState("");
  const { token } = useToken();
  const {messages} = useState([]);
  //const { state } = useLocation();
  const cardArray = [];
  let chatSocket = useRef();

  const handleChat = (message) => {
    if (message) {
      chatSocket.current.emit("message", {
        text: message,
        token: token
      })
    }
  }

  useEffect(() => {
    console.log("Length: ",messages.length)

    chatSocket.current = io("http://localhost:3003/chat");
    chatSocket.current.on("text", (message) => {
      console.log(message)
      messages.push(message)
      console.log("Length: ",messages.length)

    })
  }, [])

  return (
    <div>
      <div className="Game">
        {/* <img src="/img/abstract_scene.svg" className="Card" height="200" width="150"/> */}
        <svg id="svg" xmlns="http://www.w3.org/2000/svg">
          {cardArray}
        </svg>
      </div>
      <div className="Chat">
        <ChatBox messages={messages.current} onSubmit={handleChat} />
      </div>
    </div>
  );
}
