import React, { useState, useEffect } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import "../styles/Game.css";
import axios from "axios";
import { useLocation } from "react-router";
import 'react-chatbox-component/dist/style.css';
import { ChatBox } from 'react-chatbox-component';
import io from "socket.io-client";


export default function Game(roomID) {
  const [room, setRoom] = useState("");
  const { state } = useLocation();

  const handleChat = (message) => {
    console.log("NICE:", message)
  }

  const messages = [
    {
      "text": "Hello there",
      "sender": {
        "name": "Ironman",
        "uid": "user1",
        "avatar": "https://singlecolorimage.com/get/000000/400x100",
      },
    },
  ]

  useEffect(() => {
    const socket = io("http://localhost:3003/chat");
    console.log(socket)
  }, [])

  return (
    <div>
      <div className="Game">
        <img src="/img/abstract_scene.svg" height="200" width="150"/>
      </div>
      <div className="Chat">
        <ChatBox messages={messages} onSubmit={handleChat}/>
      </div>
    </div>
  );
}
