import React, { useState, useEffect, useRef } from "react";
//import Form from "react-bootstrap/Form";
//import Button from "react-bootstrap/Button";
import "../styles/Game.css";
import axios from "axios";
import * as constants from './Constants';
//import { useLocation } from "react-router";
import 'react-chatbox-component/dist/style.css';
import { ChatBox } from 'react-chatbox-component';
import io from "socket.io-client";
//import $ from "jquery";
import useToken from "../components/useToken";
import jwtDecode from "jwt-decode";



export default function Game(roomID) {
  //const [room, setRoom] = useState("");
  const { token } = useToken();
  const [messages, setMessages] = useState([]);
  //Modal Data
  const [show, setShow] = useState(false);
  const [message, setMessage] = useState("")
  //const { state } = useLocation();
  const cardArray = [];
  let chatSocket = useRef();
  const user = {}

  // Code that is called when chat is sent
  const handleChat = (message) => {
    if (message) {
      console.log(user)
      chatSocket.current.emit("message", {
        text: message,
        token: token
      });
    }
  }
  // Code that is run immediately 
  useEffect(() => {
    chatSocket.current = io("http://localhost:3003/chat");
    chatSocket.current.on("text", (message) => {
      setMessages( array => [...array, message])
    })
    async function startWebSocket() {
      try {
        const response = await axios.post(constants.HOST + "/game", {}, {
          headers: {
            "Authorization": "Bearer " + token
          }
        });
        user.uid = await jwtDecode(token).username;
      } catch(error) {
        console.log("Invalid JWT")
      }
    }
    startWebSocket();
  }, [])

  return (
    <div>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Message</Modal.Title>
        </Modal.Header>
        <Modal.Body>{message}</Modal.Body>
        <Modal.Footer>
          <Button variant="Primary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
      <div className="Game">
        {/* <img src="/img/abstract_scene.svg" className="Card" height="200" width="150"/> */}
        <svg id="svg" xmlns="http://www.w3.org/2000/svg">
          {cardArray}
        </svg>
      </div>
      <div className="Chat">
        <ChatBox messages={messages} user={user} onSubmit={handleChat} />
      </div>
    </div>
  );
}
